mapper.select:
select m.memberno as memberno, si.sitterno as sitterno, name, FLOOR( (CAST(REPLACE(current_date,'-','') AS unsigned) - CAST(REPLACE(birthdate,'-','') as unsigned)) / 10000 ) + 2 AS birthdate, payment, applydate, phone, accept, deny_type from connect conn
inner join mom_info mi on mi.momno = conn.momno inner join sitter_info si on si.sitterno = conn.sitterno inner join member m on m.memberno=si.memberno
where mi.memberno=#{memberno}
